mapper.select:
SELECT zz_sys_user.* FROM zz_sys_user_role, zz_sys_user
AND zz_sys_user_role.role_id = #{roleId} AND zz_sys_user_role.user_id = zz_sys_user.user_id
ORDER BY ${orderBy}

SELECT * FROM zz_sys_user
NOT EXISTS (SELECT * FROM zz_sys_user_role WHERE zz_sys_user_role.role_id = #{roleId} AND zz_sys_user_role.user_id = zz_sys_user.user_id)
ORDER BY ${orderBy}
