mapper.select:
SELECT pm.module_id moduleId, pm.module_name moduleName, p.perm_id permId, p.perm_name permName, p.create_time createTime, p.url FROM zz_sys_user u, zz_sys_user_role ur, zz_sys_role_menu rm, zz_sys_menu_perm_code mpc, zz_sys_perm_code_perm pcp, zz_sys_perm p, zz_sys_perm_module pm
AND u.login_name = #{loginName} AND u.deleted_flag = ${@com.orange.demo.common.core.constant.GlobalDeletedFlag@NORMAL} AND u.user_id = ur.user_id AND ur.role_id = rm.role_id AND rm.menu_id = mpc.menu_id AND mpc.perm_code_id = pcp.perm_code_id AND pcp.perm_id = p.perm_id AND p.module_id = pm.module_id AND p.deleted_flag = ${@com.orange.demo.common.core.constant.GlobalDeletedFlag@NORMAL} AND pm.deleted_flag = ${@com.orange.demo.common.core.constant.GlobalDeletedFlag@NORMAL}
AND p.url LIKE #{safeUrl}
AND p.module_id = #{moduleId}
ORDER BY pm.module_id, p.create_time

SELECT
    p.*
FROM
    zz_sys_perm_code_perm pcp,
    zz_sys_perm p

    AND pcp.perm_code_id = #{permCodeId}
    AND pcp.perm_id = p.perm_id
    AND p.deleted_flag = ${@com.orange.demo.common.core.constant.GlobalDeletedFlag@NORMAL}


    ORDER BY ${orderBy}
