mapper.select:
SELECT * FROM zz_sys_role




            AND role_name LIKE #{safeRoleName}


    AND deleted_flag = ${@com.orange.demo.common.core.constant.GlobalDeletedFlag@NORMAL}


    ORDER BY ${orderBy}

SELECT DISTINCT r.* FROM my_sys_role r, my_sys_role_menu rm, my_sys_menu_perm_code mpc
mpc.perm_code_id = #{permCodeId} AND mpc.menu_id = rm.menu_id AND rm.role_id = r.role_id AND r.deleted_flag = ${@com.orange.demo.common.core.constant.GlobalDeletedFlag@NORMAL}
ORDER BY r.create_time

SELECT DISTINCT r.* FROM my_sys_role r, my_sys_role_menu rm, my_sys_menu_perm_code mpc, my_sys_perm_code_perm pcp, my_sys_perm p
p.url LIKE #{safeUrl} AND p.perm_id = pcp.perm_id AND pcp.perm_code_id = mpc.perm_code_id AND mpc.menu_id = rm.menu_id AND rm.role_id = r.role_id AND r.deleted_flag = ${@com.orange.demo.common.core.constant.GlobalDeletedFlag@NORMAL} AND p.deleted_flag = ${@com.orange.demo.common.core.constant.GlobalDeletedFlag@NORMAL}
ORDER BY r.create_time
